<template>
    <div class="col-4 col-lg-4 col-xl-4 border-right">
			<a href="#" class="list-group-item list-group-item-action border-0" v-for="(user, index) in users" :key="index">
				<div class="d-flex align-items-start">
					<img src="data:image/png;base64," class="rounded-circle mr-1" alt="Jennifer Chang" width="40" height="40">
					<div class="flex-grow-1 ml-3">
						{{user.user}}
					</div>
				</div>
			</a>
	</div>
</template>
<script>

import axios from 'axios';
import endpoints from '../config/config.js';

export default {
   data(){
	   return {
		users: []
	   }
   },
   methods:{
	   getUsers(){
		   axios.get(endpoints.USERS).then((result) => {
			   this.users = result.data.result;
		   }).catch((err) => {
			   console.log(err)
		   });
	   }
   },mounted() {
	   this.getUsers()
   },
}
</script>
